  // Function to handle the animation sequence
  let pythonText = document.getElementById('python');
  let pythonCode = document.getElementById('python_code');
  let luaText = document.getElementById('lua');
  let luaCode = document.getElementById('lua_code');
  let phpText = document.getElementById('php');
  let phpCode = document.getElementById('php_code');
  let bashText = document.getElementById('bash');
  let bashCode = document.getElementById('bash_code');

  // Function to animate each language one by one
  function animateCode() {
    setTimeout(() => {
      pythonText.classList.add('hidden');
      pythonCode.classList.add('reveal');
    }, 1000);

    setTimeout(() => {
      pythonCode.classList.remove('reveal');
      luaText.classList.remove('hidden');
      luaCode.classList.add('reveal');
    }, 6000); // After 5 seconds

    setTimeout(() => {
      luaCode.classList.remove('reveal');
      phpText.classList.remove('hidden');
      phpCode.classList.add('reveal');
    }, 11000); // After 10 seconds

    setTimeout(() => {
      phpCode.classList.remove('reveal');
      bashText.classList.remove('hidden');
      bashCode.classList.add('reveal');
    }, 16000); // After 15 seconds

    setTimeout(() => {
      bashCode.classList.remove('reveal');
      pythonText.classList.remove('hidden');
      pythonCode.classList.add('reveal');
    }, 21000); // After 20 seconds
  }

  // Start the animation and loop it
  animateCode();
  setInterval(animateCode, 25000); // Loop the sequence
